CREATE TABLE DEPT (
    DEPTNO  INT PRIMARY KEY,
    DNAME   CHAR(14),
    LOC     CHAR(13)
);

CREATE TABLE EMP (
    EMPNO     INT PRIMARY KEY,
    ENAME     CHAR(10),
    JOB       CHAR(9),
    MGR       INT,
    HIREDATE  DATE,
    SAL       NUMERIC(7, 2),
    COMM      NUMERIC(7, 2),
    DEPTNO    INT
);

CREATE TABLE UniDAC_BLOB (
  ID INT PRIMARY KEY,
  Title CHAR(30)--,
--  Picture PICTURE
);

CREATE TABLE UniDAC_TEXT (
  ID INT PRIMARY KEY,
  Title CHAR(30),
  TextField MEMO
);

CREATE TABLE UniDAC_Loaded (
  Intg INT,
  Dbl  DOUBLE,
  Str  CHAR(50),
  Dat  DATE
);

CREATE TABLE CRGRID_TEST (
  Id INT PRIMARY KEY,
  Name CHAR(10),
  Country CHAR(30),
  City CHAR(30),
  Street CHAR(30),
  BirthDate DATE,
  Job CHAR(9),
  Hiredate DATE,
  Sal NUMERIC(7, 2),
  Remarks MEMO
);

